class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        a = nums
        n = len(a)
        if all(x==0 for x in a):
            return 0
        d = [1]*n
        for i, q in enumerate(queries):
            x, y, z = q
            for j in range(x, y+1):
                d[j] |= d[j] << z
            if all(d[j] & (1 << a[j]) for j in range(n)):
                return i+1
        return -1

