    static boolean[] sieveOfEratosthenes(int n){
        boolean[] p = new boolean[n+1];
        Arrays.fill(p, true);
        p[0] = p[1] = false;
        for(int i = 2; i * i <= n; i++){
            if(p[i]){
                for(int j = i * i; j <= n; j += i)
