            }
        }
        return p;
    }

    static void sort(int[] a){
        List<Integer> l = new ArrayList<>();
        for(int x : a) l.add(x);
        Collections.sort(l);
        for(int i = 0; i < a.length; i++)
            a[i] = l.get(i);
    }

    static long modPow(long a, long b, long m){
        long r = 1;
        a %= m;
        while(b > 0){
            if((b & 1) != 0)
                r = r * a % m;
            b >>= 1;
            a = a * a % m;
        }
        return r;
    }

    static class FastReader {
