    public static int minZeroArray(int[] a, int[][] q) {
        boolean z = true;
        for (int x : a) {
            if(x != 0) { z = false; break; }
        }
        if(z) return 0;
        int l = 1, r = q.length, ans = -1;
        while(l <= r) {
            int mid = l + (r - l) / 2;
            if(canZeroArray(a, q, mid)) {
                ans = mid;
                r = mid - 1;
            } else {
                l = mid + 1;
            }
        }
        return ans;
    }

    private static boolean canZeroArray(int[] a, int[][] q, int k) {
        int n = a.length;
        for (int j = 0; j < n; j++) {
            int tgt = a[j];
            boolean[] dp = new boolean[tgt + 1];
            dp[0] = true;
            for (int i = 0; i < k; i++) {
                if(q[i][0] <= j && j <= q[i][1]) {
                    int v = q[i][2];
                    for (int s = tgt; s >= v; s--) {
                        if(dp[s - v]) dp[s] = true;
                    }
                }
            }
            if(!dp[tgt]) return false;
        }
        return true;
    }
}

