class Solution {
	public int minZeroArray(int[] nums, int[][] queries) {
		int n = nums.length;
		int max = 0;
		outer:
		for(int i = 0;i < n;i++){
			int v = nums[i];
			boolean[] dp = new boolean[v+1];
			dp[0] = true;
			if(v == 0){
				max = Math.max(max, 0);
				continue;
			}
			for(int j = 0;j < queries.length;j++){
				int[] q = queries[j];
				if(q[0] <= i && i <= q[1]){
					for(int k = v-q[2];k >= 0;k--){
						if(dp[k]){
							dp[k+q[2]] = true;
						}
					}
				}
				if(dp[v]){
					max = Math.max(max, j+1);
					continue outer;
				}
			}
			return -1;
		}
		return max;
	}
}

