class Solution {
public:
    int dp[15][1005], n, q, a[1005];
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        n = nums.size();
        for(int i = 1; i <= n; ++i) a[i] = nums[i - 1];
        for(int i = 1; i <= n; ++i) dp[i][0] = 1;
        q = queries.size();
        int zero = 0;
        for(int i = 1; i <= n; ++i) if(a[i] == 0) ++zero;
        if(zero == n) return 0;
        for(int i = 0; i < q; ++i){
            int l = queries[i][0], r = queries[i][1], v = queries[i][2];
            for(int j = l + 1; j <= r + 1; ++j){
                for(int k = a[j]; k >= v; --k)
                    dp[j][k] = std::max(dp[j][k], dp[j][k - v]);
            }
            int ok = 0;
            for(int j = 1; j <= n; ++j)
                if(dp[j][a[j]] == 1)
                    ok += 1;
            if(ok == n) return i + 1;
        }
        return -1;
    }
};

