from typing import List

class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n, m = len(nums), len(queries)
        def possible(k: int) -> bool:
            for i in range(n):
                target, dp = nums[i], 1
                for j in range(k):
                    l, r, v = queries[j]
                    if l <= i <= r:
                        dp |= dp << v
                        if dp >> target & 1:
                            break
                if not (dp >> target & 1):
                    return False
            return True
        lo, hi, ans = 0, m + 1, -1
        while lo < hi:
            mid = (lo + hi) // 2
            if possible(mid):
                ans, hi = mid, mid
            else:
                lo = mid + 1
        return ans if ans != m + 1 else -1

