class Solution {
    fun minZeroArray(nums: IntArray, queries: Array<IntArray>): Int {
        if (nums.isAllZero()) return 0

        val queryList = queries.map { Query(it[0], it[1], it[2]) }
        var (low, high) = 1 to queryList.size
        var result = -1
        while (low <= high) {
            val mid = (low + high) / 2
            if (isAchievableAt(mid, nums, queryList)) {
                result = mid
                high = mid - 1
            } else {
                low = mid + 1
            }
        }
        return result
    }

    private data class Query(val start: Int, val end: Int, val value: Int)

    private fun IntArray.isAllZero() = all { it == 0 }

    private fun List<Int>.canSumToTarget(target: Int) =
        fold(setOf(0)) { sums, num ->
            sums.union(sums.map { it + num }.filter { it <= target })
        }.contains(target)

    private fun isAchievableAt(
        k: Int,
        nums: IntArray,
        queries: List<Query>
    ): Boolean =
        nums.indices.all { i ->
            val available = (0..<k).mapNotNull { j ->
                if (queries[j].start <= i && i <= queries[j].end) queries[j].value else null
            }
            available.sum() >= nums[i] && available.canSumToTarget(nums[i])
        }

}

