from functools import lru_cache

class Solution:
    def minZeroArray(self, a: list[int], qs: list[list[int]]) -> int:
        n = len(a)

        def br():
            rg = [[] for _ in range(n)]
            for i, (L, R, c) in enumerate(qs):
                for j in range(L, R + 1):
                    rg[j].append(i)
            return rg

        def fm(qL, k):
            @lru_cache(None)
            def rec(i, k):
                if k == 0:
                    return i
                if i == len(qL) or k < 0:
                    return 10**9
                return min(rec(i + 1, k), rec(i + 1, k - qs[qL[i]][2]))
            return rec(0, k)

        rg = br()
        res = 0
        for i in range(n):
            tmp = fm(rg[i], a[i])
            if tmp == 10**9:
                return -1
            if tmp > 0 and (tmp - 1) < len(rg[i]):
                res = max(res, rg[i][tmp - 1] + 1)
        return res

