#include <vector>
using namespace std;

class Solution {
public:
    int minZeroArray(vector<int>& A, vector<vector<int>>& B) {
        int a = A.size();
        bool b = true;
        for (int c = 0; c < a; c++) {
            if (A[c] != 0) {
                b = false;
                break;
            }
        }
        if (b) {
            return 0;
        }

        vector<vector<bool>> d(a);
        for (int e = 0; e < a; e++) {
            d[e].resize(A[e] + 1, false);
            d[e][0] = true;
        }

        int f = B.size();
        for (int g = 0; g < f; g++) {
            int h = B[g][0];
            int i = B[g][1];
            int j = B[g][2];

            for (int k = h; k <= i; k++) {
                int l = A[k];
                vector<bool> m = d[k];
                for (int n = 0; n <= l; n++) {
                    if (d[k][n]) {
                        if (n + j <= l) {
                            m[n + j] = true;
                        }
                    }
                }
                d[k] = m;
            }

            bool o = true;
            for (int p = 0; p < a; p++) {
                if (!d[p][A[p]]) {
                    o = false;
                    break;
                }
            }
            if (o) {
                return g + 1;
            }
        }
        return -1;
    }
};

