class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size();
        auto canZero = [&](int k) -> bool {
            for (int j = 0; j < n; j++) {
                vector<bool> dp(nums[j] + 1, false);
                dp[0] = true;
                for (int i = 0; i < k; i++) {
                    int l = queries[i][0], r = queries[i][1], val = queries[i][2];
                    if (j < l || j > r) continue;
                    for (int s = nums[j]; s >= 0; s--) {
                        if (dp[s] && s + val <= nums[j])
                            dp[s + val] = true;
                    }
                }
                if (!dp[nums[j]])
                    return false;
            }
            return true;
        };
        if (canZero(0))
            return 0;
        for (int k = 1; k <= m; k++) {
            if (canZero(k))
                return k;
        }
        return -1;
    }
};

