class Solution {
    fun minZeroArray(a: IntArray, q: Array<IntArray>): Int {
        val dp = q
        val n = a.size
        val L = Array(n) { mutableListOf<Pair<Int, Int>>() }
        for(j in dp.indices){
            val l = dp[j][0]
            val r = dp[j][1]
            val v = dp[j][2]
            for(i in l..r) L[i].add(j to v)
        }
        fun ok(k: Int): Boolean {
            for(i in 0 until n){
                val t = a[i]
                if(t == 0) continue
                val arr = L[i].filter { it.first < k }.map { it.second }
                val dp = BooleanArray(t + 1)
                dp[0] = true
                for(x in arr){
                    for(s in t downTo x) if(dp[s - x]) dp[s] = true
                    if(dp[t]) break
                }
                if(!dp[t]) return false
            }
            return true
        }
        if(a.all { it == 0 }) return 0
        var lo = 1
        var hi = dp.size
        var ans = -1
        while(lo <= hi){
            val mid = (lo + hi) / 2
            if(ok(mid)){
                ans = mid
                hi = mid - 1
            } else lo = mid + 1
        }
        return ans
    }
}

