class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;

        int[] ansz = nums.clone();

        boolean alrdyz = true;
        for (int num : ansz) {
            if (num != 0) {
                alrdyz = false;
                break;
            }
        }
        if (alrdyz) return 0;


        boolean[][] dp = new boolean[n][];
        for (int j = 0; j < n; j++) {
            dp[j] = new boolean[ansz[j] + 1];
            dp[j][0] = true;
        }


        for (int i = 0; i < queries.length; i++) {
            int l = queries[i][0], r = queries[i][1], val = queries[i][2];
            for (int j = l; j <= r; j++) {

                for (int s = ansz[j]; s >= val; s--) {
                    if (!dp[j][s] && dp[j][s - val]) {
                        dp[j][s] = true;
                    }
                }
            }

            boolean allz = true;
            for (int j = 0; j < n; j++) {
                if (!dp[j][ansz[j]]) {
                    allz = false;
                    break;
                }
            }
            if (allz) {
                return i + 1;
            }
        }

        return -1;
    }
}

