class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)

        if all(x == 0 for x in nums):
            return 0

        vals = [[] for _ in range(n)]

        def subsumpos(vals: List[int], tar: int) -> bool:
            dp = [False] * (tar + 1)
            dp[0] = True
            for val in vals:
                for s in range(tar, val - 1, -1):
                    if dp[s - val]:
                        dp[s] = True
                if dp[tar]:
                    return True
            return dp[tar]

        for k, query in enumerate(queries, start=1):
            l, r, val = query
            for i in range(l, r + 1):
                vals[i].append(val)

            possible = True
            for i in range(n):
                if nums[i] == 0:
                    continue
                if not subsumpos(vals[i], nums[i]):
                    possible = False
                    break

            if possible:
                return k

        return -1

