const int M = 1005;

int dp[10][M];

class Solution {
public:
    int minZeroArray(vector<int>& a, vector<vector<int>>& qs) {
        int n = a.size();
        int q = qs.size();

        int f = 1;
        for (int i = 0; i < n; i++) {
            if (a[i]) {
                f = 0;
                break;
            }
        }
        if (f) return 0;

        for (int i = 0; i < n; i++) {
            for (int j = 1; j <= a[i]; j++) dp[i][j] = 0;
            dp[i][0] = 1;
        }

        for (int ii = 0; ii < q; ii++) {
            int l = qs[ii][0], r = qs[ii][1], v = qs[ii][2];

            for (int i = l; i <= r; i++) {
                for (int j = a[i]; j >= v; j--) {
                    if (dp[i][j - v]) dp[i][j] = 1;
                }
            }

            int f = 1;
            for (int i = 0; i < n; i++) {
                if (!dp[i][a[i]]) {
                    f = 0;
                    break;
                }
            }
            if (f) {
                return ii + 1;
            }
        }

        return -1;
    }
};

