class Solution {
public:
    int minZeroArray(vector<int>& a, vector<vector<int>>& q) {

        int n = a.size(), m = q.size(), ans = 0;

        for (int i = 0; i < n; i++) {

            int t = a[i];
            vector<bool> dp(t + 1, false);

            dp[0] = true;
            int cur = -1;

            if(t == 0) cur = 0;
            else {
                for (int j = 0; j < m; j++){
                    if(q[j][0] <= i && i <= q[j][1]){
                        int v = q[j][2];
                        for (int k = t; k >= v; k--)
                            dp[k] = dp[k] || dp[k - v];
                    }
                    if(dp[t]){
                        cur = j + 1;
                        break;
                    }
                }
            }
            if(cur == -1) return -1;
            ans = max(ans, cur);
        }

        return ans;
    }
};

