class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int ans = 0;
        for (int i = 0; i < nums.size(); ++i) {
            int target = nums[i];
            bitset<1001> cur;
            cur[0] = true;

            if (target == 0) continue;

            int at = -1;
            for (int j = 0; j < queries.size(); ++j) {
                if (i < queries[j][0] || i > queries[j][1]) continue;
                cur = cur | (cur << queries[j][2]);
                if (cur[target]) {
                    at = j;
                    break;
                }
            }
            if (at == -1) return -1;
            ans = max(ans, at + 1);
        }
        return ans;
    }
};

