bool f(int t, vector<int>& v) {
    vector<bool> d(t + 1, false);
    d[0] = true;
    for (auto x : v)
        for (int j = t; j >= x; j--)
            if (d[j - x]) d[j] = true;
    return d[t];
}

bool g(int k, vector<int>& v, vector<vector<int>>& q) {
    int n=v.size();
    for (int i = 0; i < n; i++) {
        vector<int> x;
        for (int j = 0; j < k; j++)
            if (q[j][0] <= i && i <= q[j][1]) x.push_back(q[j][2]);
        if (!f(v[i], x)) return 0;
    }
    return 1;
}

class Solution {
public:
    int minZeroArray(vector<int>& v, vector<vector<int>>& q) {
        int m=q.size();
        for (int k = 0; k< m+1; k++)
            if (g(k,v, q)) return k;
        return -1;
    }
};

