    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        m = len(queries)
        if all(x == 0 for x in nums):
            return 0

        def check(k):
            for j in range(n):
                dp = 1
                for i in range(k):
                    l, r, v = queries[i]
                    if l <= j <= r:
                        dp |= dp << v
                if (dp >> nums[j]) & 1 == 0:
                    return False
            return True

        low, high = 0, m + 1
        while low < high:
            mid = (low + high) // 2
            if check(mid):
                high = mid
            else:
                low = mid + 1
        return low if low <= m else -1

