class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:

        n = len(nums)

        if all(x == 0 for x in nums):
            return 0

        non_zero_indices = [j for j in range(n) if nums[j] > 0]
        if not non_zero_indices:
            return 0

        MAX_SUM = 1000
        CLAMP_MASK = (1 << (MAX_SUM + 1)) - 1  
        bitset_of = [1 for _ in range(n)]  

        for i, (left, right, val) in enumerate(queries):
            for j in range(left, right + 1):
                if nums[j] > 0:  
                    bitset_of[j] |= (bitset_of[j] << val)
                    bitset_of[j] &= CLAMP_MASK  

            all_zero_possible = True
            for j in non_zero_indices:
                if not ((bitset_of[j] >> nums[j]) & 1):
                    all_zero_possible = False
                    break

            if all_zero_possible:
                return i + 1  

        return -1

