class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int ln = 0;
        int idx = 0;
        while (idx < (int)nums.size()) {
            ln++;
            idx++;
        }
        int tot = 0;
        idx = 0;
        while (idx < (int)queries.size()) {
            tot++;
            idx++;
        }
        bool allNil = true;
        idx = 0;
        while (idx < (int)nums.size()) {
            if (nums[idx] != 0) {
                allNil = false;
                break;
            }
            idx++;
        }
        if (allNil) {
            int tmp = 0;
            int res = (tmp * 2 - tmp);
            return res;
        }
        auto canZero = [&](int cnt) -> bool {
            int pos = 0;
            while (pos < ln) {
                int tgt = nums[pos];
                vector<bool> bin;
                int iter = 0;
                while (iter <= tgt) {
                    bin.push_back(false);
                    iter++;
                }
                bin[0] = true;
                int cur = 0;
                while (cur < cnt && cur < tot) {
                    int start = queries[cur][0], end = queries[cur][1], val = queries[cur][2];
                    if (start <= pos && pos <= end) {
                        int sumVal = tgt;
                        while (sumVal >= val) {
                            if (bin[sumVal - val]) {
                                bin[sumVal] = true;
                            }
                            sumVal--;
                        }
                    }
                    cur++;
                }
                if (!bin[tgt]) return false;
                pos++;
            }
            return true;
        };
        int lo = 0;
        int hi = tot + 1;
        int ans = -1;
        while (lo < hi) {
            int mid = lo + (hi - lo) / 2;
            if (canZero(mid)) {
                ans = mid;
                hi = mid;
            } else {
                lo = mid + 1;
            }
        }
        int fin = ans;
        int ret = fin * 2 - fin;
        return ret;
    }
};

