} T;


int sol2(T* P, int cnt, int m, int tar) {
    char d[10001] = { 0 };
    d[0] = 1;
    for (int i = 0; i < cnt; i++) {
        if (P[i].x < m) {
            int v = P[i].y;
            for (int j = tar; j >= v; j--) {
                if (d[j - v]) d[j] = 1;
            }
        }
    }
    return d[tar];
}


int minZeroArray(int* A, int N, int** Q, int QS, int* QC) {
    int i, j, f = 1;
    for (i = 0; i < N; i++) {
        if (A[i] != 0) { f = 0; break; }
    }
    if (f) return 0;
    T** B = (T**)malloc(N * sizeof(T*));
    int* C = (int*)malloc(N * sizeof(int));
    for (i = 0; i < N; i++) {
        B[i] = (T*)malloc(QS * sizeof(T));
        C[i] = 0;
    }
    for (j = 0; j < QS; j++) {
        int L = Q[j][0], R = Q[j][1], V = Q[j][2];
        for (i = L; i <= R; i++) {
            B[i][C[i]].x = j;
            B[i][C[i]].y = V;
            C[i]++;
        }
    }
    int lo = 0, hi = QS, ans = -1;
    while (lo <= hi) {
        int mid = (lo + hi) / 2, ok = 1;
        for (i = 0; i < N; i++) {
            if (A[i] == 0) continue;
            if (!sol2(B[i], C[i], mid, A[i])) { ok = 0; break; }
        }
        if (ok) { ans = mid; hi = mid - 1; }
        else { lo = mid + 1; }
    }
    for (i = 0; i < N; i++) free(B[i]);
    free(B); free(C);
    return ans;
}

