        vector<bool> dp(n + 1, 0);
        dp[n] = 1;
        for(int x: vec)
            for(int i = x; i <= n; i++)
                if(dp[i]) dp[i - x] = 1;
        return dp[0];
    }
    bool ok(vector<int>& a, vector<vector<int>>& qr, int k){
        vector<vector<int>> vec(a.size(), vector<int>());
        for(int i = 0; i < k; i++){
            int l = qr[i][0];
            int r = qr[i][1] + 1;
            int x = qr[i][2];
            for(int j = l; j < r; j++) vec[j].push_back(x);
        }
        bool res = 1;
        for(int i = 0; i < a.size(); i++) res &= can(vec[i], a[i]);
        return res;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int tl = -1, tr = queries.size() + 1;
        while(tr - tl > 1){
            int tm = tl + tr >> 1;
            if(ok(nums, queries, tm)) tr = tm;
            else tl = tm;
        }
        if(tr > queries.size()) return -1;
        return tr;
    }
};

