                    # can decrement
                    for k in range(nums[i], val-1, -1):
                        if possible[k-val]:
                            possible[k] = True
                            possible_after[k] = min(possible_after[k], j+1)
            if not possible[-1]:
                return -1
            max_needed = max(max_needed, possible_after[-1])
        return max_needed

