            if (i <= q[j][1])
            {
              dp1 = dp1 | (dp1 << q[j][2]);
            }
          }
        }

        if (!dp1.test(n[i]))
        {
          ok = false;
          break;
        }

      }
      if (ok)
      {
        return k;
      }
    }

    return -1;
  }
};

