class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int N = nums.size();
        const int K = 1005;
        vector<vector<bool>> can(N, vector<bool>(K,0));

        for (int i = 0; i < N; ++i) can[i][nums[i]] = 1;


        int qn = 0;
        auto can_zero = [&]() -> bool {
            for (int i = 0; i < N; ++i) if (!can[i][0]) return false;
            return true;
        };
        for (vector<int>& qq : queries) {
            if (can_zero()) return qn;
            ++qn;
            int l = qq[0];
            int r = qq[1];
            int val = qq[2];

            for (int i = l; i <= r; ++ i) {
                for (int j = 0; j+val < K; ++j) {
                    can[i][j] = can[i][j] || can[i][j+val];
                }
            }

        }
        if (can_zero()) return qn;
        return -1;
    }
};

