impl Solution {
    pub fn min_zero_array(nums: Vec<i32>, queries: Vec<Vec<i32>>) -> i32 {
        let n = nums.len();
        let len = queries.len();
        let mut f = |m: usize| -> bool {
            for j in 0..n {
                let r = nums[j] as usize;
                if r != 0 {
                    let mut dp = vec![false; r + 1];
                    dp[0] = true;
                    for i in 0..m {
                        if queries[i][0] as usize <= j && j <= queries[i][1] as usize {
                            let v = queries[i][2] as usize;
                            for s in (v..=r).rev() {
                                dp[s] |= dp[s - v];
                            }
                        }
                    }
                    if !dp[r] {
                        return false;
                    }
                }
            }
            true
        };
        // println!("{}", f(0));
        // println!("{}", f(1));
        // println!("{}", f(2));
        let mut low = 0;
        let mut high = len + 1;
        while low < high {
            let mid = (low + high) / 2;
            if f(mid) {
                high = mid;
            } else {
                low = mid + 1;
            }
        }
        let ans: i32 = if low == len + 1 { -1 } else { low as i32 };
        // println!("{}", ans);

        ans
    }
