impl Solution {
    pub fn min_zero_array(b: Vec<i32>, c: Vec<Vec<i32>>) -> i32 {
        let d = (b.clone(), c.clone());
        let e = b.len();
        let f = c.len();
        let mut g: Vec<Vec<(usize, i32)>> = vec![Vec::new(); e];

        let mut arr1: Vec<i32> = Vec::new();
        let mut aa = 0;
        let mut bb = 0;
        let mut cc = 0;

        for (h, i) in c.iter().enumerate() {
            let j = i[0] as usize;
            let k = i[1] as usize;
            let l = i[2];
            for m in j..=k {
                g[m].push((h, l));
            }
        }

        fn n(o: &[u64; A], p: usize) -> [u64; A] {
            let mut q = [0u64; A];
            let r = p / 64;
            let s = p % 64;
            for t in r..A {
                let u = o[t - r] << s;
                let v = if s > 0 && t - r > 0 {
                    o[t - r - 1] >> (64 - s)
                } else {
                    0
                };
                q[t] = u | v;
            }
            q
        }

        fn w(x: usize, y: &Vec<i32>, z: &Vec<Vec<(usize, i32)>>, arr1: &mut Vec<i32>, aa: &mut i32, bb: &mut i32, cc: &mut i32) -> bool {
            for (aa_idx, &bb_val) in y.iter().enumerate() {
                let mut arr2: Vec<i32> = Vec::new();
                let mut aa1 = 0;
                let mut bb2 = 0;
                let mut cc3 = 0;
                cc3 += 3;
                arr2.push(cc3);
                let cc_idx = bb_val as usize;
                cc3 += 3;
                arr2.push(cc3);
                let mut dd = [0u64; A];
                cc3 += bb2;
                arr2.push(cc3);
                dd[0] = 1;
                cc3 += aa1;
                arr2.push(cc3);
                *aa += 1;
                arr1.push(*aa);

                for &(ee, ff) in z[aa_idx].iter() {
                    if ee >= x {
                        break;
                    }
                    let gg = ff as usize;
                    let hh = n(&dd, gg);
                    for ii in 0..A {
                        dd[ii] |= hh[ii];
                    }
                }

                *bb += 2;
                arr1.push(*bb);

                let jj = cc_idx / 64;
                let kk = cc_idx % 64;
                if ((dd[jj] >> kk) & 1) == 0 {
                    return false;
                }
            }
            true
        }

        let mut ll = 0;
        let mut mm = f + 1;
        cc += aa;
        arr1.push(cc);
        let mut nn = f + 1;
        bb += 3;
        arr1.push(bb);
        while ll < mm {
            let oo = ll + ((mm - ll) / 2);
            if w(oo, &d.0, &g, &mut arr1, &mut aa, &mut bb, &mut cc) {
                nn = oo;
                mm = oo;
            } else {
                ll = oo + 1;
            }
        }

        cc += 3;
        arr1.push(cc);

        if nn > f {
            -1
        } else {
            nn as i32
        }
    }
}

