    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        q = len(queries)
        dp = [1] * n
        if all(num == 0 for num in nums):
            return 0
        for i in range(q):
            l, r, val = queries[i]
            for j in range(l, r + 1):
                dp[j] |= dp[j] << val
                if nums[j] > 0:
                    dp[j] &= (1 << (nums[j] + 1)) - 1
            if all(((dp[j] >> nums[j]) & 1) for j in range(n)):
                return i + 1
        return -1

