class Solution {
public:
    bool isZeroAchievable(const vector<int>& nums, const vector<vector<int>>& queries, int qCount) {
        int n = nums.size();
        vector<vector<int>> coinBuckets(n);
        for (int i = 0; i < qCount; i++) {
            int l = queries[i][0], r = queries[i][1], coin = queries[i][2];
            for (int j = l; j <= r; j++) {
                coinBuckets[j].push_back(coin);
            }
        }
        for (int i = 0; i < n; i++) {
            int target = nums[i];
            vector<bool> dp(target + 1, false);
            dp[0] = true;
            for (int c : coinBuckets[i]) {
                for (int s = target; s >= c; s--) {
                    if (dp[s - c]) dp[s] = true;
                }
            }
            if (!dp[target]) return false;
        }
        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int low = 0, high = queries.size() + 1, ans = -1;
        while (low < high) {
            int mid = low + (high - low) / 2;
            if (isZeroAchievable(nums, queries, mid)) {
                ans = mid;
                high = mid;
            } else {
                low = mid + 1;
            }
        }
        return ans;
    }
};

