class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int ret = 0;
        for (int i = 0; i < nums.size(); i++) if (nums[i] > 0) {
            int ans = -1;

            int u = nums[i];
            vector<bool> cur(u + 1), nxt(u + 1);

            for (int z = 0; z <= u; z++) {
                cur[z] = nxt[z] = false;
            }
            cur[0] = true;

            for (int idx = 0; idx < queries.size(); idx++) {
                int l = queries[idx][0], r = queries[idx][1], val = queries[idx][2];
                if (i < l || r < i) {
                    continue;
                }

                for (int x = 0; x <= u; x++) if (cur[x]) {
                    nxt[x] = true;
                    if (x + val <= u) {
                        nxt[x + val] = true;                    
                    }
                }

                if (nxt[u]) {
                    ans = idx + 1;
                    break;
                }

                for (int x = 0; x <= u; x++) {
                    cur[x] = nxt[x];
                    nxt[x] = false;
                }
            }

            if (ans < 0) {
                return ans;
            }
            ret = max(ret, ans);
        }
        return ret;
    }
};

