#include <bits/stdc++.h>
using namespace std;

class Solution {
public:
    // Checks if 'target' can be formed as a sum of a subset of values[] (each used at most once)
    bool canFormSum(int target, const vector<int>& values) {
        // If target < 0, impossible
        if (target < 0) return false;
        // A quick check: if sum of all values < target, no point in DP
        int sumVals = 0;
        for (int v : values) sumVals += v;
        if (sumVals < target) return false;

        // Classic bitset-based subset-sum: dp[s] = can we make sum = s ?
        static const int MAX_SUM = 10000 * 100; // placeholder if needed
        // Actually, we will use target + 1 as the bitset range
        vector<bool> dp(target + 1,false);
        dp[0] = true; 
        for (int v : values) {
            // Go backward so we don't reuse 'v' more than once
            for (int s = target; s >= v; s--) {
                if (dp[s - v]) dp[s] = true;
            }
        }
        return dp[target];
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = (int)nums.size();
        int m = (int)queries.size();

        // For k from 0..m, check if we can become zero
        for (int k = 0; k <= m; k++) {
            bool canBeZero = true;

            // For each index j, gather all query-values that cover j among first k queries
            for (int j = 0; j < n; j++) {
                int needed = nums[j];
                if (needed == 0) continue;  // already zero, no subset needed

                vector<int> vals;
                // Collect vali for i in [0..k-1] if j in [l_i, r_i]
                for (int i = 0; i < k; i++) {
                    int li = queries[i][0], ri = queries[i][1], val = queries[i][2];
                    if (li <= j && j <= ri) {
                        vals.push_back(val);
                    }
                }

                // Now check if we can form 'needed' from subset of vals
                if (!canFormSum(needed, vals)) {
                    canBeZero = false;
                    break;
                }
            }

            if (canBeZero) {
                return k;  // The first k that works is our answer
            }
        }

        // If none worked:
        return -1;
    }
};

