class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int ans = 0;
        for (int i = 0; i < nums.length; i++) {
            int val = nums[i];
            int rounds = 0;
            boolean[] dp = new boolean[val + 1];
            dp[val] = true;
            for (int j = 0; j < queries.length && !dp[0]; j++) {
                if (i >= queries[j][0] && i <= queries[j][1]) {
                    int delta = queries[j][2];
                    for (int k = delta; k <= val; k++) {
                        if (dp[k]) dp[k - delta] = true;
                    }
                }
                rounds++;
            }
            if (!dp[0]) return -1;
            ans = Math.max(ans, rounds);
        }
        return ans;
    }
}

