        if (n == 0) return 0;
        vector<bool> dp(n + 1);
        dp[0] = true;
        for (int j = 0; j < queries.size(); j++) {
            auto& q = queries[j];
            if (q[0] > i || q[1] < i) continue;
            int v = q[2];
            for (int t = n; t >= v; t--) {
                dp[t] = dp[t] || dp[t - v];
            }
            if (dp[n]) return j + 1;
        }
        return -1;
    }
};

