                    if(x < 0 || j == sz(v[i])) return 0;
                    int& ret = dp[x][j];
                    if(~ret) return ret;
                    ret = self(self, x, j + 1);
                    ret = max(ret, self(self, x - v[i][j], j + 1));
                    return ret;
                };
                if(rec(rec, a[i], 0)) continue;
                return 0;
            }
            return 1;
        };


        int start = 0, end = sz(q), mid, ans = -1;
