class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int m = queries.size();
        int lo = 0, hi = m + 1;
        while (lo < hi) {
            int mid = lo + (hi - lo) / 2;
            if (check(mid, nums, queries)) hi = mid;
            else lo = mid + 1;
        }
        return lo <= m ? lo : -1;
    }
    bool check(int k, vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        for (int j = 0; j < n; j++) {
            vector<int> vals;
            for (int i = 0; i < k; i++) {
                if (queries[i][0] <= j && j <= queries[i][1])
                    vals.push_back(queries[i][2]);
            }
            if (!subset(vals, nums[j])) return false;
        }
        return true;
    }
    bool subset(vector<int>& vals, int t) {
        vector<bool> taans2(t + 1, false);
        taans2[0] = true;
        for (int v : vals) {
            for (int s = t - v; s >= 0; s--) {
                if (taans2[s]) taans2[s + v] = true;
            }
        }
        return taans2[t];
    }
};

