class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        N = len(nums)
        Q = len(queries)

        def f(x, vs):
            dp = [False] * (x + 1)
            dp[0] = True
            for v in vs:
                for y in range(x, -1, -1):
                    if y + v <= x:
                        dp[y + v] |= dp[y]
            return dp[x]

        def check(nq):
            vs = [[] for _ in range(N)]
            for q in range(nq):
                l, r, v = queries[q]
                for j in range(l, r + 1):
                    vs[j].append(v)

            for i in range(N):
                if not f(nums[i], vs[i]):
                    return False

            return True

        if check(0):
            return 0
        if not check(Q):
            return -1
        lo, hi = 0, Q
        while hi - lo > 1:
            mid = (lo + hi) // 2
            if check(mid):
                hi = mid
            else:
                lo = mid
        return hi

