                    v.pb(j);
            if(!sz(v)) return -1;
            bool dp[1010], ok = 0;
            memset(dp,0,sizeof(dp));
            dp[0] = 1;
            for(int j = 0; j < sz(v); j++){
                int x = q[v[j]][2];
                for(int k = a[i]; k>=x; k--) dp[k]|=dp[k-x];
                if(dp[a[i]]){
                    ans = max(ans, v[j]+1);ok = 1;
                    break;
                }
            }
            if(!ok) return -1;
        }
        return ans;
    }
};

