class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int ans = 0;
        for(int i = 0; i < nums.size(); i++) {
            vector<int> minturn(1001, 1);
            int doneat = -1;
            int num = nums[i];
            minturn[num] = 0;
            if (num == 0) continue;
            int qc = 0;
            for(auto &q : queries) {
                qc++;
                // for(int i = 0; i < 30; i++) cout << minturn[i] << ' '; cout << endl;
                int l = q[0], r = q[1], d = q[2];
                if (l <= i && i <= r) {
                    for(int i = d; i <= 1000; i++) {
                        if (minturn[i] != 1) {
                            minturn[i - d] = 0;
                            if (i - d == 0) {
                                doneat = qc;
                                break;
                            }
                        }
                    }
                    if (doneat != -1) break;
                }
            }
            // cout << i << ' ' << doneat << endl;
            if (doneat == -1) return -1;
            else {
                ans = max(ans, doneat);
            }
        }
        return ans;
    }
};

