class Solution {
public:
    bool subsetSum(vector<int>& nums, int target) 
    {
        int n = nums.size();
        vector<bool> dp(target + 1, false);
        dp[0] = true;  

        for (int num : nums) 
        {
            for (int j = target; j >= num; j--) 
            {  
                dp[j] = dp[j] || dp[j - num];
            }
        }

        return dp[target];
    }


    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) 
    {
        int n = nums.size();
        int l = 0;
        int r = queries.size();
        int ans = -1;

        while(l<=r)
            {
                int mid = l + (r - l)/2;

                vector<vector<int>>lineSweep(n+1);

                for(int i = 0; i<mid; i++)
                    {
                        auto&q = queries[i];
                        int l = q[0], r = q[1], val = q[2];

                        for(int j = l; j<=r; j++)
                            {
                                lineSweep[j].push_back(val);
                            }
                    }

                bool cond = true;
                for(int i = 0; i<n; i++)
                    {
                        vector<int>v = lineSweep[i];
                        int sum = nums[i];
                        if(!subsetSum(v,sum))
                        {
                            cond = false;
                            break;
                        }
                    }

                if(cond)
                {
                    r = mid - 1;
                    ans = mid;
                }
                else l = mid+1;
            }

        return ans;

    }
};

