            feasible = True
            for j in range(n):
                available = []
                for i in range(k):
                    li, ri, vali = queries[i]
                    if li <= j <= ri:
                        available.append(vali)

                target = nums[j]

                dp = [False] * (target + 1)
                dp[0] = True
                for v in available:
                    for s in range(target, v - 1, -1):
                        if dp[s - v]:
                            dp[s] = True
                    if dp[target]:
                        break

                if not dp[target]:
                    feasible = False
                    break

            if feasible:
                return k

        return -1

