            int md = l + (h - l) / 2;
            if (canZero(md, arr, queries)) {
                h = md;
            } else {
                l = md + 1;
            }
        }
        return (l <= m) ? l : -1;
    }

     public boolean canZero(int k, int[] nums, int[][] queries) {
        int n = nums.length;
        for (int i = 0; i < n; i++) {
            int t = nums[i];
            List<Integer> arr = new ArrayList<>();
            for (int j = 0; j < k; j++) {
                if (queries[j][0] <= i && i <= queries[j][1]) {
                    arr.add(queries[j][2]);
                }
            }
            if (t == 0) {
                continue;
            }
            if (!subsetSum(arr, t)) {
                return false;
            }
        }
        return true;
    }
    private boolean subsetSum(List<Integer> arr, int t) {
        boolean[] dp = new boolean[t + 1];
        dp[0] = true;
        for (int v : arr) {
            for (int s = t; s >= v; s--) {
                if (dp[s - v]) {
                    dp[s] = true;
                }
            }
        }
        return dp[t];
    }
}

