class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        boolean[][] dp = new boolean[n][];
        for(int i = 0; i < n; i++){
            int target = nums[i];
            dp[i] = new boolean[target + 1];
            dp[i][0] = true;
        }
        boolean isZero = true;
        for(int num : nums){
            if(num != 0){
                isZero = false;
                break;
            }
        }
        if(isZero) return 0;
        for(int k = 0; k < queries.length; k++){
            int l = queries[k][0];
            int r = queries[k][1];
            int val = queries[k][2];

            for(int i = l; i <= r; i++){
                if(nums[i] == 0) continue;
                for(int s = dp[i].length - 1; s >= val; s--){
                    if(!dp[i][s] && dp[i][s - val]) dp[i][s] = true;
                }
            }
            boolean isSatisfied = true;
            for(int i = 0; i < n; i++){
                if(!dp[i][nums[i]]){
                    isSatisfied = false;
                    break;
                }
            }
            if(isSatisfied){
                return k + 1;
            }
        }
        return -1;
    }
}

