class Solution {
public:
    lli fuc(lli i,lli j,lli ind,vector<vector<lli>>&dp,vector<vector<int>>& queries){
        lli n=queries.size();
        if(i==n){
            if(j==0){
                return n;
            }
            return 1e18;
        }
        if(dp[i][j]!=-1){
            return dp[i][j];
        }
        if(j==0){
            return dp[i][j]=i;
        }
        lli ans=fuc(i+1,j,ind,dp,queries);
        if(ind>=queries[i][0]&&ind<=queries[i][1]&&j>=queries[i][2]){
            ans=min(ans,fuc(i+1,j-queries[i][2],ind,dp,queries));
        }
        return dp[i][j]=ans;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n=nums.size();
        int ans=0;
        for(int i=0;i<nums.size();i++){
            vector<vector<lli>>dp(queries.size(),vector<lli>(nums[i]+1,-1));
            lli t=fuc(0,nums[i],i,dp,queries);
            if(t>queries.size()){
                return -1;
            }
            ans=max(ans,(int)(t));
        }
        return ans;
    }
};

