class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int a = nums.size();
        bool b = true;
        for (int c : nums) {
            if (c != 0) { b = false; break; }
        }
        if (b) return 0;
        int d = queries.size();
        int e = 0, f = d + 1;
        while (e < f) {
            int g = e + (f - e) / 2;
            vector<int> h = nums;
            bool i = true;
            for (int j = 0; j < a; j++) {
                vector<int> k;
                for (int l = 0; l < g; l++) {
                    int m = queries[l][0], n = queries[l][1], o = queries[l][2];
                    if (m <= j && j <= n)
                        k.push_back(o);
                }
                int p = h[j];
                bitset<1100> q;
                q.reset();
                q[0] = 1;
                for (int r : k)
                    q |= (q << r);
                if (!q[p]) {
                    i = false;
                    break;
                }
            }
            if (i)
                f = g;
            else
                e = g + 1;
        }
        return (e <= d ? e : -1);
    }
};

