class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size();

        bool alreadyZero = true;
        for (int num : nums) {
            if (num != 0) {
                alreadyZero = false;
                break;
            }
        }
        if (alreadyZero)
            return 0;

        int low = 1, high = m, ans = -1;
        while (low <= high) {
            int mid = low + (high - low) / 2;
            if (feasible(nums, queries, mid)) {
                ans = mid;
                high = mid - 1;
            } else {
                low = mid + 1;
            }
        }
        return ans;
    }

private:
    bool feasible(const vector<int>& nums, const vector<vector<int>>& queries,
                  int k) {
        int n = nums.size();

        for (int j = 0; j < n; j++) {
            int target = nums[j];

            bitset<1001> dp;
            dp.reset();
            dp[0] = 1;

            for (int i = 0; i < k; i++) {
                int l = queries[i][0], r = queries[i][1], v = queries[i][2];
                if (l <= j && j <= r) {

                    dp |= (dp << v);
                }
            }
            if (!dp[target])
                return false;
        }
        return true;
    }
};

