#include <vector>
#include <unordered_map>
#include <algorithm>
#include <climits>

using namespace std;

class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<vector<int>> q(n);

        for (int j = 0; j < queries.size(); ++j) {
            for (int i = queries[j][0]; i <= queries[j][1]; ++i) {
                q[i].push_back(j);
            }
        }

        vector<int> result(n, -1);
        for (int i = 0; i < n; ++i) {
            if (nums[i] == 0) continue;

            vector<int>& curr_queries = q[i];
            if (curr_queries.empty()) return -1;

            int total = 0;
            for (int j : curr_queries) total += queries[j][2];
            if (total < nums[i]) return -1;

            unordered_map<int, int> dp;
            dp[0] = -1;
            bool found = false;
            int earliest = INT_MAX;

            for (int pos = 0; pos < curr_queries.size(); ++pos) {
                int j = curr_queries[pos];
                int value = queries[j][2];
                unordered_map<int, int> new_dp = dp;

                for (auto& entry : dp) {
                    int sum = entry.first;
                    int new_sum = sum + value;
                    if (new_sum == nums[i]) {
                        earliest = min(earliest, j);
                        found = true;
                    }
                    if (new_sum <= nums[i] && new_dp.find(new_sum) == new_dp.end()) {
                        new_dp[new_sum] = j;
                    }
                }
                dp = new_dp;
                if (found) break;
            }

            if (found) result[i] = earliest;
            else if (dp.find(nums[i]) != dp.end()) result[i] = dp[nums[i]];
            else return -1;
        }

        vector<int> ks;
        for (int i = 0; i < n; ++i) {
            if (result[i] != -1) ks.push_back(result[i] + 1);
        }
        return ks.empty() ? 0 : *max_element(ks.begin(), ks.end());
    }
};

