class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:

        dp = {}
        res = []
        need = len(nums)
        for i, num in enumerate(nums):
            if num == 0:
                res.append(True)
                need -= 1
            else:
                res.append(False)
                dp[i] = set([0])

        if need == 0:
            return 0

        for idx in range(len(queries)):
            l, r, val = queries[idx]
            for i in range(l, r + 1):
                if not res[i]:

                    ncands = set()
                    for cand in dp[i]:
                        if cand + val == nums[i]:
                            res[i] = True
                            need -= 1
                            break
                        if cand + val < nums[i]:
                            ncands.add(cand + val)

                    dp[i] |= ncands

            if need == 0:
                return idx + 1
        return -1

