class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& qry) {
        int n = nums.size(), m = qry.size();
        bool ok = true;
        for (int i = 0; i < n; i++) { if(nums[i] != 0){ ok = false; break; } }
        if(ok) return 0;
        int lo = 0, hi = m + 1;
        while(lo < hi) {
            int mid = (lo + hi) / 2;
            if(check(nums, qry, mid)) hi = mid; else lo = mid + 1;
        }
        return (lo > m ? -1 : lo);
    }
private:
    bool check(const vector<int>& nums, const vector<vector<int>>& qry, int k) {
        int n = nums.size();
        for (int i = 0; i < n; i++) {
            vector<int> arr;
            for (int j = 0; j < k; j++) {
                int l = qry[j][0], r = qry[j][1], v = qry[j][2];
                if(l <= i && i <= r) arr.push_back(v);
            }
            if(!sub(nums[i], arr)) return false;
        }
        return true;
    }
    bool sub(int tar, const vector<int>& arr) {
        vector<bool> dp(tar + 1, false);
        dp[0] = true;
        for (int v : arr) {
            for (int s = tar; s >= v; s--) {
                if(dp[s - v]) dp[s] = true;
            }
        }
        return dp[tar];
    }
};

