class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        res = 0
        for i, num in enumerate(nums):
            if num == 0:
                continue
            dp, mask = 1, (1<<num+1) - 1
            for k, (l, r, val) in enumerate(queries):
                if l <= i <= r:
                    dp |= dp << val
                    dp &= mask
                    if dp & (1<<num):
                        res = max(res, k+1)
                        break
            else:
                return -1
        return res

